#include <iostream>
#include<cassert>
#include<vector>
#include<numeric>
#include<memory>
#include<algorithm>
#include<chrono>

template<typename T>
typename T::size_type LevenshteinDistanceMemory(T source,
                                          T target) {
    if (source.size() > target.size()) {
        return LevenshteinDistanceMemory(target, source);
    }

    using TSizeType = typename T::size_type;
    const std::unique_ptr<TSizeType> min_size(new TSizeType(source.size())), max_size(new TSizeType(target.size()));
    std::unique_ptr<std::vector<TSizeType>> lev_dist(new std::vector<TSizeType>(*min_size.get() + 1));

    std::iota(lev_dist.get()->begin(), lev_dist.get()->end(), 0);

    for (TSizeType j = 1; j <= *max_size.get(); ++j) {
        TSizeType previous_diagonal = (*lev_dist.get())[0], previous_diagonal_save;
        ++(*lev_dist.get())[0];

        for (TSizeType i = 1; i <= *min_size.get(); ++i) {
            previous_diagonal_save = (*lev_dist.get())[i];
            if ((source[i - 1] == target[j - 1])) {
                (*lev_dist.get())[i] = previous_diagonal;
            } else {
                (*lev_dist.get())[i] = std::min(std::min((*lev_dist.get())[i - 1], (*lev_dist.get())[i]), previous_diagonal) + 1;
            }
            previous_diagonal = previous_diagonal_save;
        }
    }

    return (*lev_dist.get())[*min_size.get()];
}

template<typename T>
typename T::size_type levenshteinDistance(const T& source, const T& target) {

    using TSizeType = typename T::size_type;

    TSizeType m = source.length();
    TSizeType n = target.length();

    if (m < n) {
        return levenshteinDistance(target, source);
    }

    std::vector<TSizeType> prevRow(n + 1);
    std::vector<TSizeType> currentRow(n + 1);

    std::iota(prevRow.begin(), prevRow.end(), 0);

    for (TSizeType i = 1; i <= m; ++i) {
        currentRow[0] = i;
        for (TSizeType j = 1; j <= n; ++j) {
            TSizeType cost = (source[i - 1] == target[j - 1]) ? 0 : 1;
            currentRow[j] = std::min({currentRow[j - 1] + 1, prevRow[j] + 1, prevRow[j - 1] + cost});
        }
        prevRow = currentRow;
    }

    return currentRow[n];
}

// Вычисляет длины самых длинных общих подстроковых массивов (LCS) двух строк
std::vector<std::vector<int>> lcs_lengths(const std::string& a, const std::string& b) {
    int n = a.size();
    int m = b.size();

    // Создаем матрицу размером (n+1)x(m+1) для хранения длин LCS
    std::vector<std::vector<int>> lengths(n + 1, std::vector<int>(m + 1));

    // Заполняем матрицу длинами LCS для всех пар подстрок a[0..i] и b[0..j]
    for (int i = 0; i <= n; ++i) {
        for (int j = 0; j <= m; ++j) {
            if (i == 0 || j == 0) {
                // Базовый случай: если одна из строк пуста, то LCS пуст
                lengths[i][j] = 0;
            } else if (a[i - 1] == b[j - 1]) {
                // Если текущие символы строк равны, то LCS равен LCS предыдущих подстрок плюс текущий символ
                lengths[i][j] = lengths[i - 1][j - 1] + 1;
            } else {
                // Если текущие символы строк не равны, то LCS равен максимальной длине LCS предыдущих подстрок
                lengths[i][j] = std::max(lengths[i - 1][j], lengths[i][j - 1]);
            }
        }
    }

    return lengths;
}

// Вычисляет расстояние Левенштейна между двумя строками с использованием LCS для оптимизации
int levenshtein_distance_with_lcs(const std::string& source, const std::string& target) {
    if (source.empty()) {
        // Базовый случай: если одна из строк пуста, то расстояние Левенштейна равно длине другой строки
        return target.size();
    }

    if (target.empty()) {
        return source.size();
    }

    auto lcs_lengths_matrix = lcs_lengths(source, target);
    int n = source.size();
    int m = target.size();

    int prev_row = 0;
    int curr_row = 0;

    for (int j = 1; j <= m; ++j) {
        prev_row = curr_row;
        curr_row = j;

        int diagonal = curr_row - 1;

        for (int i = 1; i <= n; ++i) {
            int cost = (source[i - 1] == target[j - 1]) ? 0 : 1;

            // Вычисляем минимальное расстояние Левенштейна между текущими подстроками с использованием LCS для оптимизации
            int min_dist = std::min({lcs_lengths_matrix[i][diagonal], lcs_lengths_matrix[prev_row][curr_row], lcs_lengths_matrix[i][prev_row]}) + cost;
            lcs_lengths_matrix[i][curr_row] = min_dist;

            diagonal = curr_row;
        }
    }

    // Возвращаем расстояние Левенштейна между исходными строками
    return lcs_lengths_matrix[n][m];
}



void test_func()
{
    auto start1 = std::chrono::high_resolution_clock::now();
    assert(LevenshteinDistanceMemory<std::string>("ARMA", "ALKATRAS") == 6);
    assert(LevenshteinDistanceMemory<std::string>("b", "") == 1);
    assert(LevenshteinDistanceMemory<std::string>("ammmmmmmooooooooogggggggggguuuuuuuuuussssssss", "kalahmatkadakldkqwdjqfjsdhjldhfqklfwfhlhfejkwrflwnfhkwejukoikjhefndwzs") == 66);
    assert(LevenshteinDistanceMemory<std::string>("biba", "boba") == 1);
    assert(LevenshteinDistanceMemory<std::string>("avstria", "avstralia") == 2);
    assert(LevenshteinDistanceMemory<std::string>("kotik", "skotina") == 3);
    assert(LevenshteinDistanceMemory<std::string>("", "") == 4096);
    auto end1 = std::chrono::high_resolution_clock::now();
    auto duration1 = std::chrono::duration_cast<std::chrono::milliseconds>(end1 - start1);
    std::cout << "Execution time: " << duration1.count() << " ms" << std::endl;

    auto start = std::chrono::high_resolution_clock::now();
    assert(levenshteinDistance<std::string>("ARMA", "ALKATRAS") == 6);
    assert(levenshteinDistance<std::string>("b", "") == 1);
    assert(levenshteinDistance<std::string>("ammmmmmmooooooooogggggggggguuuuuuuuuussssssss", "kalahmatkadakldkqwdjqfjsdhjldhfqklfwfhlhfejkwrflwnfhkwejukoikjhefndwzs") == 66);
    assert(levenshteinDistance<std::string>("biba", "boba") == 1);
    assert(levenshteinDistance<std::string>("avstria", "avstralia") == 2);
    assert(levenshteinDistance<std::string>("kotik", "skotina") == 3);
    assert(levenshteinDistance<std::string>("", "") == 4096);
    auto end = std::chrono::high_resolution_clock::now();
    auto duration = std::chrono::duration_cast<std::chrono::milliseconds>(end - start);
    std::cout << "Execution time: " << duration.count() << " ms" << std::endl;

    std::cout << "Tests Success!\n";
}

int main()
{
    test_func();
    return 0;
}
